const e=document.getElementById("popup-form-overlay"),s=document.getElementById("close-popup");s?.addEventListener("click",()=>{e&&e.classList.remove("active")});e?.addEventListener("click",t=>{t.target===e&&e.classList.remove("active")});document.addEventListener("keydown",t=>{t.key==="Escape"&&e?.classList.contains("active")&&e.classList.remove("active")});document.querySelectorAll(".js-trigger-popup").forEach(t=>{t.addEventListener("click",c=>{c.preventDefault(),e?.classList.add("active")})});
